function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }
    //Type appropriate comment here, and begin script below

    var enderecoJSON = new GlideAjax('CEPAutomationInc');
    enderecoJSON.addParam('sysparams_cep', g_form.getValue('u_cep'));
    enderecoJSON.getXML(function(response) {

        var tmpData = response.responseXML.documentElement.getAttribute('answer');
		alert(tmpData);

        if (tmpData != null) {
            var resultJSON = JSON.parse(tmpData);
            displayMessage(resultJSON[0].displayValue + 'updated', 'success');
            buildResultsTable();
        } else {
            displayMessage('Records successfully updated', 'error');
        }
    });

