sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {

    // Add your code here
    try {
        var r = new sn_ws.RESTMessageV2('global.CEPAutomation', 'GetCep');
        r.setStringParameterNoEscape('cepViaCep', current.u_cep);

        var response = r.execute();
        var responseBody = response.getBody();
        var httpStatus = response.getStatusCode();


		// Convert JSON formatted string to a JavaScript Object

		var responseObj = JSON.parse(responseBody);

		// Set thye value of address fields on the response record

		current.u_enrececo = responseObj.logradouro;
		current.u_bairro = responseObj.bairro;
		current.u_cidade = responseObj.localidade;
		current.u_estado = responseObj.uf;
		gs.info("EndereÃ§o = " + current.u_endereco);

		current.update();


    } catch (err) {
        // var message = ex.message;
		gs.info("uncaught error: " + err);
    }
})(current, previous);
