sys_script_include.script:
var CEPAutomationInc = Class.create();
CEPAutomationInc.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

    getCepJSON: function() {

        try {
			var cepCandidato = this.getParameter('sysparams_cep');

            var r = new sn_ws.RESTMessageV2('global.CEPAutomation', 'GetCep');
            r.setStringParameterNoEscape('cepViaCep', cepCandidato);

            var response = r.execute();
            var responseBody = response.getBody();
            var httpStatus = response.getStatusCode();

        } catch (err) {
            gs.info("uncaught error: " + err);
        }
        return responseBody;
    },

    type: 'CEPAutomationInc'
});
